import Vue from "vue";
import {
	request,
	navigateTo,
	showToast,
	showModal,
	onShareAppMessage
} from "@/common/utils/util";
import {
	OrderListGet,
	OrderPay,
	OrderConfirmReceiving
} from "@/common/config/api";
import MescrollUni from "@/components/mescroll-diy/mescroll-beibei.vue";
import mediaList from "@/components/order-list/order-list.vue";

import wx from "jweixin-module"; // 微信公众号（H5）JSSDK

export default Vue.extend({
	components: {
		MescrollUni,
		mediaList
	},
	data() {
		return {
			current: "",
			OrderStatus: "",
			orderList: [], // 订单列表
			productID: "", // 选择产品ID
			orderID: "", // 选择订单ID
			PayTypeID: 1, // 支付类型ID 微信小程序支付传1
			tabBars: [
				{
					name: "全部",
					id: 0,
					status: "-1"
				},
				{
					name: "待填地址",
					id: 1,
					status: "0"
				},
				{
					name: "待付款",
					id: 2,
					status: "1"
				},
				{
					name: "待发货",
					id: 3,
					status: "2"
				},
				{
					name: "待收货",
					id: 4,
					status: "3"
				},
				{
					name: "待晒单",
					id: 5,
					status: "4"
				}
			],
			mescroll: null
		};
	},
	onLoad(options: any) {
		console.log(options);
		let status = options.status;
		this.current = status;
		this.OrderStatus = status;




		document.body.addEventListener("touchmove", (e: any) => {
			document.body.scrollTop = 0;
		});

	},
	onShow() {
		if (this.mescroll) {
			let mescroll: any = this.mescroll;
			this.downCallback(mescroll);
		}
	},
	onShareAppMessage(e: any) {
		return onShareAppMessage(e);
	},
	methods: {
		// 点击选项卡
		tapTab(e: any) {
			this.current = e;
			let mescroll: any = this.mescroll;
			this.downCallback(mescroll);
		},
		/*下拉刷新的回调 */
		downCallback(mescroll: any) {
			// 下拉刷新的回调,默认重置上拉加载列表为第一页 (自动执行 mescroll.num=1, 再触发upCallback方法 )
			this.mescroll = mescroll;
			mescroll.resetUpScroll();
		},
		/*上拉加载的回调: mescroll携带page的参数, 其中num:当前页 从1开始, size:每页数据条数,默认10 */
		upCallback(mescroll: any) {
			//联网加载数据
			this.getListDataFromNet(
				mescroll.num,
				mescroll.size,
				(curPageData: any) => {
					//联网成功的回调,隐藏下拉刷新和上拉加载的状态;
					mescroll.endSuccess(curPageData.length);
					//设置列表数据
					if (mescroll.num == 1) this.orderList = []; //如果是第一页需手动制空列表
					this.orderList = this.orderList.concat(curPageData); //追加新数据
					console.log("orderList", curPageData, this.orderList);
				},
				() => {
					//联网失败的回调,隐藏下拉刷新的状态
					mescroll.endErr();
				}
			);
		},
		/*联网加载列表数据
			实际项目以您服务器接口返回的数据为准,无需本地处理分页.
			* */
		async getListDataFromNet(
			pageNum: any,
			pageSize: any,
			successCallback: any,
			errorCallback: any
		) {
			try {
				let orderList: any = await this.getOrderList(pageNum, pageSize);
				//联网成功的回调
				successCallback && successCallback(orderList);
			} catch (e) {
				//联网失败的回调
				errorCallback && errorCallback();
			}
		},
		getOrderList(pageNum: any, pageSize: any) {
			return new Promise((sesolve, reject) => {
				let OrderStatus = this.current;
				if (OrderStatus == "-1") {
					let data = {
						PageID: pageNum,
						PageSize: pageSize
					};
					request(OrderListGet, data)
						.then((res: any) => {
							sesolve(res.OrderList);
						})
						.catch((err: any) => {
							let mescroll: any = this.mescroll;
							mescroll.endErr();
						});
				} else {
					let data = {
						OrderStatus: OrderStatus,
						PageID: pageNum,
						PageSize: pageSize
					};
					request(OrderListGet, data)
						.then((res: any) => {
							sesolve(res.OrderList);
						})
						.catch((err: any) => {
							let mescroll: any = this.mescroll;
							mescroll.endErr();
						});
				}
			});
		},
		order(e: any) {
			this.orderID = e;
		},
		product(e: any) {
			this.productID = e;
		},
		async action(e: any) {
			console.log("action", e);
			switch (e) {
				case "填写地址":
					let productID = this.productID;
					let disabled: boolean = true;
					let orderID = this.orderID;
					navigateTo(
						"../addressShipping/addressShipping?disabled=" +
							disabled +
							"&orderID=" +
							orderID +
							"&productID=" +
							productID
					);
					break;
				case "确认收货":
					let res: any = await this.orderConfirmReceiving();
					let mescroll = this.mescroll;
					if (res.IsError) {
						showToast(res.ErrMsg);
					} else {
						showToast("确认收货成功！");
						this.downCallback(mescroll);
					}
					break;






				case "去支付":
					this.paymentH5();
					break;

				case "晒单":
					navigateTo(
						"../orderDryingUpload/orderDryingUpload?id=" +
							this.orderID +
							"&OrderID=" +
							this.orderID
					);
					break;
			}
		},
		goDetail(e: any) {
			console.log(e);
			navigateTo(
				"../orderDetail/orderDetail?productID=" + e.ID + "&OrderID=" + e.OrderID
			);
		},
		// 晒单上传
		goOrderDrying(e: any) {
			console.log(e);
			navigateTo(
				"../orderDryingUpload/orderDryingUpload?productID=" +
					e.ID +
					"&OrderID=" +
					e.OrderID
			);
		},
		// 订单确认收货
		orderConfirmReceiving() {
			return new Promise((sesolve, reject) => {
				let OrderID = this.orderID;
				let data = {
