import Vue from "vue";
import { request, navigateTo, showToast, defaultShowModal } from "@/common/utils/util";
import {
	ProductGet,
	OrderExchangeSubmit,
	UserAddressListGet
} from "@/common/config/api";
import uParse from "@/components/uParse/src/wxParse.vue";
export default Vue.extend({
	components: {
		uParse
	},
	data() {
		return {
			id: "",
			addressID: "",
			article: "",
      integrals: "",
      userInfo: "",
			addressList: "",
			isShow: false
		};
	},
	onLoad(options: any) {
		this.id = options.id;
		this.integrals = options.integrals;
		console.log("onLoad", options);
  },
  onShow() {
    this.getProduct();
  },
	methods: {
		async getProduct() {
			let ProductID = this.id;
			let data = {
				ProductID: ProductID
			};
			request(ProductGet, data).then((res: any) => {
				this.article = res.Product.ProductDesc;
      });
      this.addressID = uni.getStorageSync("addressID");
      this.userInfo = uni.getStorageSync("UserInfo")
			let address: any = await this.getUserAddressList();
      this.addressList = address.AddressList;
		},
		preview(src: any, e: any) {
			// do something
			console.log("src: " + src);
		},
		navigate(href: any, e: any) {
			// 如允许点击超链接跳转，则应该打开一个新页面，并传入href，由新页面内嵌webview组件负责显示该链接内容
			console.log("href: " + href);
			uni.showModal({
				content: "点击链接为：" + href,
				showCancel: false
			});
		},
		show() {
			this.isShow = true;
		},
		getUserAddressList() {
			return new Promise((resolve, reject) => {
				let AddressID = this.addressID;
				let data = {
					AddressID: AddressID
				};
				request(UserAddressListGet, data).then((res: any) => {
					resolve(res);
				});
			});
		},
		exchangeSubmit() {
			let ExchangeID = this.id;
			let AddressID = this.addressID;
			if (!AddressID) {
				let msg: string = "你还没有默认地址，请点击确定去填写地址信息！";
				defaultShowModal(msg).then((res: any) => {
					console.log(res);
					if (res.confirm) {
						console.log("用户点击确定");
						navigateTo("../../ucenter/addressShipping/addressShipping");
					} else if (res.cancel) {
						console.log("用户点击取消");
					}
				});
			}
			let data = {
				ExchangeID: ExchangeID,
				AddressID: AddressID
			};
			request(OrderExchangeSubmit, data).then((res: any) => {
				this.isShow = false;
				let msg: string = "兑换成功，请点击确定查看订单！";
        defaultShowModal(msg).then((res: any) => {
					console.log(res);
					if (res.confirm) {
						console.log("用户点击确定");
						navigateTo("../../ucenter/orderList/orderList");
					} else if (res.cancel) {
						console.log("用户点击取消");
					}
				});
				console.log(res);
			});
    },
    hide() {
      this.isShow = false;
    },
    moveHandle() {
      this.isShow = false;
    },
    addressClick() {
      navigateTo("../../ucenter/addressShipping/addressShipping");
    }
	}
});
