import Vue from "vue";
import { mapState, mapMutations } from "vuex";
import {
	request,
	navigateTo,
	navigateBack,
	showToast
} from "@/common/utils/util";
import { UserLogin, GetLoginCode, GetWXOpenID } from "@/common/config/api";
import uniIcon from "@/components/uni-icon/uni-icon.vue";
export default Vue.extend({
	components: {
		uniIcon
	},
	computed: {
		...mapState(["hasLogin"])
	},
	data() {
		return {
			wxFace: "",
			wxNick: "",
			refUserID: "",
			logo: "/static/icon/icon_login.png",
			checked: true,
			color: "#fe7f00",
			focus: true,
			Mobile: "",
			LoginCode: "",
			loading: false,
			time: "获取验证码",
			currentTime: 60,
			disabled: false,
			interval: ""
		};
	},
	onLoad(options: any) {},
	onUnload() {
		let interval: any = this.interval;
		clearInterval(interval);
	},
	methods: {
		...mapMutations(["login"]),
		getProvider() {
			return new Promise((sesolve, reject) => {
				uni.getProvider({
					service: "oauth",
					success: (res: any) => {
						sesolve(res.provider);
					}
				});
			});
		},
		async login() {
			let provider: any = await this.getProvider();
			return new Promise((sesolve, reject) => {
				if (~provider.indexOf("weixin")) {
					uni.login({
						provider: "weixin",
						success: (loginRes: any) => {
							let JSCode = loginRes.code;
							console.log(JSCode);
							let data = {
								JSCode: JSCode
							};
							request(GetWXOpenID, data).then((res: any) => {
								sesolve(res.OpenID);
							});
						}
					});
				}
			});
		},
		mobileInput(e: any) {
			this.Mobile = e.detail.value;
		},
		codeInput(e: any) {
			this.LoginCode = e.detail.value;
		},
		getCode(e: any) {
			let Mobile = this.Mobile;
			if (!Mobile) {
				showToast("手机号码不能为空！");
				return;
			}
			this.getLoginCode();
			let currentTime: any = this.currentTime;
			this.time = currentTime + "s后可重发";
			this.disabled = true;
			let interval: any = setInterval(() => {
				this.time = currentTime - 1 + "s后可重发";
				currentTime--;
				if (currentTime <= 0) {
					clearInterval(interval);
					this.time = "重新获取";
					this.currentTime = 60;
					this.disabled = false;
				}
			}, 1000);
			this.interval = interval;
		},
		getLoginCode() {
			let Mobile = this.Mobile;
			let data = {
				Mobile: Mobile
			};
			request(GetLoginCode, data).then((res: any) => {
				console.log(res);
				showToast("发送成功！");
			});
		},
		radioClick() {
			this.checked = !this.checked;
		},
		async wxLogin(e: any) {
			console.log(e);
			let userInfo = e.detail.userInfo;
			this.wxFace = userInfo.avatarUrl;
			this.wxNick = userInfo.nickName;
			let OpenID: any = await this.login();
			uni.setStorageSync("OpenID", OpenID);
			this.userLogin();
		},
		async userLogin() {
			let Mobile = this.Mobile;
			// let Mobile = '13723750893';
			let LoginCode = this.LoginCode;
			let wxFace = this.wxFace;
			let wxNick = this.wxNick;
			let refUserID = this.refUserID;
			if (uni.getStorageSync("scene")) {
				refUserID = uni.getStorageSync("scene");
			}
			if (!Mobile) {
				showToast("手机号码不能为空！");
				return;
			}
			if (!LoginCode) {
				showToast("验证码不能为空！");
				return;
			}
			this.loading = true;



			let data = {
				Mobile: Mobile,
				LoginCode: LoginCode,



				wxFace: wxFace,
				wxNick: wxNick,
				RefUserID: refUserID
			};
			request(UserLogin, data).then((res: any) => {
				this.loading = false;
				let SessionKey = res.SessionKey;
				let UserInfo = res.UserInfo;
				uni.setStorageSync("SessionKey", SessionKey);
				uni.setStorageSync("UserInfo", UserInfo);
				showToast("登录成功！");
				navigateBack(1);
			});
		},
		getuserinfo(e: any) {
			console.log(e);
		},
		protocol() {
			navigateTo("../protocol/protocol");
		}
	}
});
