import Vue from "vue";
import { request, navigateTo, navigateBack, showToast } from "@/common/utils/util";
import {
	UserAddressListGet,
	UserAddressDelete,
	UserAddressSetDefault
} from "@/common/config/api";
import uNo from "@/components/u-no/u-no.vue";
import uniIcon from "@/components/uni-icon/uni-icon.vue";
export default Vue.extend({
	components: {
		uNo,
		uniIcon
	},
	data() {
		return {
			addressList: [], // 用户收货地址列表
			type: "checkbox-filled", // 默认icon
			isDefault: false,
			disabled: false,
			noTitle: "您还没有可以用收货地址喔~",
			noIcon: "/static/icon/icon_no_address.png",
			isData: false, // 是否有数据
			isMaxData: false // 最多5个
		};
	},
	onLoad(options: any) {
		console.log("onLoad", options);
		this.disabled = options.disabled;
	},
	onShow() {
		this.getUserAddressList();
	},
	methods: {
		getUserAddressList() {
			let data = {};
			request(UserAddressListGet, data).then((res: any) => {
				console.log(res);
				this.addressList = res.AddressList;
				if (res.AddressList.length <= 0) {
					this.isData = true;
				}
				if (res.AddressList.length <= 4) {
					this.isMaxData = true;
				}
			});
		},
		// 设置当前收货地址为默认的
		setDefaultAddress(id: any) {
			let AddressID = id;
			let data = {
				AddressID: AddressID
			};
			request(UserAddressSetDefault, data).then((res: any) => {
				console.log(res);
				showToast("设置成功！");
				this.getUserAddressList();
				uni.setStorageSync("addressID", id);
			});
		},
		// 编辑收货地址
		editAddress(id: any) {
			navigateTo("../addressUpdate/addressUpdate?id=" + id);
		},
		// 删除收货地址
		deleteAddress(isDefault: any, id: any) {
			let AddressID = id;
			uni.showModal({
				title: "温馨提示",
				content: "是否删除收货地址！",
				confirmColor: "#fe7f00",
				success: res => {
					if (res.confirm) {
						console.log("用户点击确定");
						let data = {
							AddressID: AddressID
						};
						request(UserAddressDelete, data).then((res: any) => {
							console.log(res);
							showToast("删除成功！");
							this.getUserAddressList();
							if (isDefault === 1) {
								uni.removeStorageSync("addressID");
							}
						});
					} else if (res.cancel) {
						console.log("用户点击取消");
					}
				}
			});
		},
		// 添加地址
		addAddress() {
			let id = 0;
			navigateTo("../addressUpdate/addressUpdate?id=" + id);
		},
		// 设置存储地址
		setAddress(id: string) {
			uni.setStorageSync("addressID", id);
			navigateBack(1)
		}
	}
});
