import Vue from "vue";
import {
	request,
	navigateTo,
	formatTime,
	showToast,
	showModal,
	showErrorToast,
	defaultShowModal,
	onShareAppMessage
} from "@/common/utils/util";
import {
	ProductGet,
	PastTransactionsListGet,
	OrderDryingListGet,
	GetActiveByID,
	NextActiveGet,
	AddUserFormID
} from "@/common/config/api";
import iPast from "@/components/i-past/i-past.vue";
import iShow from "@/components/i-show/i-show.vue";
import iRule from "@/components/i-rule/i-rule.vue";
import iPopup from "@/components/i-popup/i-popup.vue";
import productTable from "@/components/product-table/product-table.vue";
let socketOpen: boolean = false;
let socketMsgQueue: any = [];
export default Vue.extend({
	components: {
		iPast,
		iShow,
		iRule,
		iPopup,
		productTable
	},
	data() {
		return {
			indicatorDots: true,
			autoplay: true,
			interval: 5000,
			duration: 500,
			circular: true,
			indicatorActiveColor: "#fe7f00",
			disabled: false, // 按钮disabled
			swiper: [], // 产品轮播
			id: "", // 商品ID
			orderID: "", // 订单ID
			product: "", // 商品
			num: "1", // 剩余次数
			pageID: 1, // 页码
			pageSize: 10, // 每页条数
			hasPastNext: false, // 是否还有往期成交下一页
			hasOrderNext: false, // 是否还有晒单下一页
			width: 0, // 设置系统宽度
			activeDetail: "", // 活动详情
			activeShow: false, // 是否有活动内容数据
			active: [], // 活动内容
			activeType: 0, // 判断是否是自动举牌还是手工举牌,activeType==0自动，1手工
			tabIndex: 0, // 默认往期成交
			pastList: [], // 往期成交列表
			showList: [], // 晒单列表
			ruleList: "", // 竞拍规则
			show: false, // 显示弹窗
			label: ["零风险", "正品承诺", "极速发货", "公平公正"],
			tab: ["往期成交", "分享晒单", "竞拍规则"],
			src: "/static/icon_experience.png",
			leading: "/static/icon/icon_leading.png",
			activeID: "", // 活动ID
			Price: "", // 价格
			msgID: "", // GUID
			msgType: "", // 消息类型
			timer: 0, // 计时器
			newCurrent: "", // 当前出价文字
			newCurrentBidder: "", // 当前领先出价人文字
			buttonsList: [], // 按钮类型，0表示报名按钮、1表示举牌按钮、2表示托管按钮、3表示填写地址按钮，4表示参与下一期按钮
			price: "", // 最新价格
			lastBills: [], // 出价列表
			timerDurationTitle: "", // 状态对应要显示的标题
			onTimerStatus: "", // 活动通知
			OnTimerDowns: "", // 成交通知
			currSignups: "", // 总已报名份数
			maxSignups: "", // 总需报名份数
			signups: "", // 我已报名份数
			seqSignups: "", // 还可报名份数
			allBills: "0", // 全部举牌次数
			myBills: "0", // 我的举牌次数,我的出价记录
			seqBills: "0", // 剩余可用次数
			seqBillsShow: false, // 显示剩余举牌次数文字
			seq: "", // 更新我的剩余举牌次数
			MyBills: "", // 举牌响应消息（包含我的举牌次数、剩余可用次数）
			SeqBills: "0", // 举牌响应消息（剩余可用次数）
			Signups: "", // 报名份数
			SeqSignups: "0", // 剩下报名份数
			mySeqBills: "0", // 我的出价记录
			UserID: "", // 登录成功，提示用户
			tapbtn: "", // 托管
			BillStat: "", // 总举牌次数
			newPrice: "", // 领先价格
			newBill: "", // 领先人全部信息
			newNick: "", // 当前领先人
			newFace: "", // 当前领先人头像
			newCity: "", // 当前领先人所在城市
			times: "", // 倒计时
			seqTime: new Date() // 初始Date时间
		};
	},
	onLoad(options: any) {
		console.log("options", options);
		this.UserID = uni.getStorageSync("UserInfo").ID;
		this.websocket();
		this.id = options.id;
		this.activeID = options.activeID;
		let windowWidth: any = uni.getSystemInfoSync().windowWidth;
		this.width = windowWidth;
	},
	onShow() {
		this.UserID = uni.getStorageSync("UserInfo").ID;
		this.websocket();
		this.getActiveByID();
		this.getPastTransactionsList();
	},
	onHide() {
		uni.hideToast();
		uni.closeSocket();
		uni.onSocketClose(res => {
			console.log("WebSocket 已关闭！");
		});
	},
	onUnload() {
		uni.hideToast();
		uni.closeSocket();
		uni.onSocketClose(res => {
			console.log("WebSocket 已关闭！");
		});
	},
	onPullDownRefresh() {
		uni.closeSocket();
		uni.onSocketClose(res => {
			console.log("WebSocket 已关闭！");
		});
		this.websocket();
		this.getActiveByID();
	},
	onReachBottom() {
		switch (this.tabIndex) {
			case 0:
				if (!this.hasPastNext) {
					this.pageID++;
					this.getPastTransactionsList();
				}
				break;
			case 1:
				if (!this.hasOrderNext) {
					this.pageID++;
					this.getOrderDryingList();
				}
				break;
		}
	},
	onShareAppMessage(e: any) {
		return onShareAppMessage(e);
	},
	methods: {
		// 获取活动详情
		getActiveByID() {
			let ActiveID = this.activeID;
			let data = {
				ActiveID: ActiveID
			};
			request(GetActiveByID, data).then((res: any) => {
				console.log("活动详情", res);
				this.activities(res.ActiveDetail);
			});
		},
		// 选项卡
		tabClick(index: number) {
			this.tabIndex = index;
			console.log(this.pageID);
			switch (index) {
				case 0:
					this.pageID = 1;
					this.hasPastNext = false;
					this.getPastTransactionsList();
					break;
				case 1:
					this.pageID = 1;
					this.hasOrderNext = false;
					this.getOrderDryingList();
					break;
			}
		},
		// 往期成交列表
		getPastTransactionsList() {
			let ActiveID = this.activeID;
			let PageID = this.pageID;
			let PageSize = this.pageSize;
			let data = {
				ActiveID: ActiveID,
				PageID: PageID,
				PageSize: PageSize
			};
			request(PastTransactionsListGet, data).then((res: any) => {
				if (PageID === 1) this.pastList = [];
				this.pastList = this.pastList.concat(res.OrderList);
				this.pastList.map((item: any) => {
					item.percentage = (
						((item.OrderPrimeCost - item.OrderMoneys) / item.OrderPrimeCost) *
						100
					).toFixed(2);
					item.CreatedTime = formatTime(new Date(item.Created));
