  import Vue from 'vue'
  import { request, navigateTo, showToast } from '@/common/utils/util'
  import { UserLogin, GetLoginCode } from '@/common/config/api'
  import uniIcon from '@/components/uni-icon/uni-icon.vue'
	export default Vue.extend({
    components: {
			uniIcon
		},
		data() {
			return {
        logo: '/static/icon/icon_login.png',
        checked: true,
        Mobile: '',
        LoginCode: '',

        time: '获取验证码',
        currentTime: 60,
        disabled: false,
        interval: ''
			}
		},
		onLoad(options) {
      console.log(options)
    },
    onUnload() {
      let interval: any = this.interval
      clearInterval(interval)
    },
		methods: {
      mobileInput(e: any) {
        console.log(e)
        this.Mobile = e.detail.value
      },
      codeInput(e: any) {
        console.log(e)
        this.LoginCode = e.detail.value
      },
      getCode(e: any) {
        console.log(e)
        let Mobile = this.Mobile
        if (!Mobile) {
          showToast('手机号码不能为空！')
          return
        }
        this.getLoginCode()
        let currentTime: any = this.currentTime
        this.time = currentTime + 's后可重发'
        this.disabled = true
        let interval: any = setInterval(() => {
          this.time = (currentTime - 1) + 's后可重发'
          currentTime--
          if (currentTime <= 0) {
            clearInterval(interval)
            this.time = '重新获取'
            this.currentTime = 60
            this.disabled = false
          }
        }, 1000)
        this.interval = interval
      },
      getLoginCode() {
        let Mobile = this.Mobile
				let data = {
					Mobile: Mobile
        }
		  	request(GetLoginCode, data).then((res: any) => {
          console.log(res)
          showToast('发送成功！')
        })
      },
      radioClick() {
        this.checked = !this.checked
      },
      userLogin() {
        let Mobile = this.Mobile
        let LoginCode  = this.LoginCode
        if (!Mobile) {
          showToast('手机号码不能为空！')
          return
        }
        if (!LoginCode) {
          showToast('验证码不能为空！')
          return
        }
				let data = {
          Mobile: Mobile,
          LoginCode: LoginCode
        }
		  	request(UserLogin, data).then((res: any) => {
          console.log(res)
          let SessionKey = res.SessionKey
          let UserInfo = res.UserInfo
          uni.setStorageSync('SessionKey', SessionKey)
          uni.setStorageSync('UserInfo', UserInfo)
          showToast('登录成功！')
        })
      },
      getuserinfo(e: any) {
        console.log(e)
      }
		}
	})
