	export default {
		props: {
			options: {
				type: Object,
				default: function(e) {
					return {}
				}
			},
			statusDone: {
				type: Boolean,
				default: false
			},
			statusFlow: {
				type: Boolean,
				default: false
			}
		},
		computed: {
			activeList: {
				let list: any = this.options
				list.map((item: any) => {
						if (item.Active.ActiveStatus === 4) {
							item.statusDone = true;
						} else if (item.Active.ActiveStatus === 5) {
							item.statusFlow = true;
						}
					});
			}
		},
		data() {
      return {
				statusIconDone: '/static/icon/icon_done.png',
				statusIconFlow: "/static/icon/icon_flow.png"
			}
    },
		methods: {
			close(e) {
				this.$emit('close');
			},
			bindClick() {
				this.$emit('click');
			}
		}
	}
