export default {
	name: "UniCountdown",
	props: {
		original: {
			type: Boolean,
			default: false
		},
		showDay: {
			type: Boolean,
			default: false
		},
		showColon: {
			type: Boolean,
			default: true
		},
		seconds: {
			type: Number,
			default: 0
		}
	},
	data() {
		return {
			timer: null,
			d: "00",
			h: "00",
			i: "00",
			s: "00",
			leftTime: 0,
			secondes: 0
		};
	},
	watch: {
		seconds(newVal, oldVal) {
			clearInterval(this.timer);
			if (newVal) {
				this.secondes = newVal;
			}
			this.countDown();
			this.timer = setInterval(() => {
				this.secondes--;
				if (this.secondes < 0) {
					this.timeUp();
					return;
				}
				this.countDown();
			}, 1000);
		}
	},
	beforeDestroy() {
		clearInterval(this.timer);
	},
	methods: {
		toSeconds(day, hours, minutes, seconds) {
			return day * 60 * 60 * 24 + hours * 60 * 60 + minutes * 60 + seconds;
		},
		timeUp() {
			clearInterval(this.timer);
			this.$emit("timeup");
		},
		countDown() {
			let seconds = this.secondes;
			let [day, hour, minute, second] = [0, 0, 0, 0];
			if (seconds > 0) {
				day = Math.floor(seconds / (60 * 60 * 24));
				hour = Math.floor(seconds / (60 * 60)) - day * 24;
				minute = Math.floor(seconds / 60) - day * 24 * 60 - hour * 60;
				second =
					Math.floor(seconds) -
					day * 24 * 60 * 60 -
					hour * 60 * 60 -
					minute * 60;
			} else {
				this.timeUp();
			}
			if (day < 10) {
				day = "0" + day;
			}
			if (hour < 10) {
				hour = "0" + hour;
			}
			if (minute < 10) {
				minute = "0" + minute;
			}
			if (second < 10) {
				second = "0" + second;
			}
			hour = +hour + day * 24;
			this.d = day;
			this.h = hour;
			this.i = minute;
			this.s = second;
		}
	}
};
