  import Vue from 'vue'
  import { request, navigateTo } from '@/common/utils/util'
	import { OrderDryingListGet } from '@/common/config/api'
	import uniLoadMore from '@/components/uni-load-more/uni-load-more.vue'
	export default Vue.extend({
		components: {
			uniLoadMore
		},
		data() {
			return {
				PageID: 1,
				PageSize: 10,
				orderList: [],
				reload: false,
				status: '',
				Totals: 0,
				contentText: {
					contentdefault: '',
					contentdown: '上拉加载更多',
					contentrefresh: '加载中',
					contentnomore: '没有更多数据了'
				}
			}
		},
		onLoad(options) {
      console.log('onLoad', options)
      this.getOrderDryingList()
		},
		onPullDownRefresh() {
			this.reload = true
			this.PageID = 1
			this.getOrderDryingList()
		},
		onReachBottom() {
			let orderList: any = this.orderList
			if (orderList.length < this.Totals) {
				//说明已有数据，目前处于上拉加载
				this.PageID++
				this.status = 'loading'
				this.reload = false
				this.status = 'more'
        this.getOrderDryingList()
			} else if (orderList.length === this.Totals) {
				this.status = 'nomore'
			}
		},
		methods: {
			getOrderDryingList() {
				let PageID = this.PageID
				let PageSize = this.PageSize
				let data = {
          PageID: PageID,
					PageSize: PageSize
				}
				request(OrderDryingListGet, data).then((res: any) => {
					this.orderList = this.reload ? res.OrderList : this.orderList.concat(res.OrderList)
					this.Totals  = res.Totals
					uni.stopPullDownRefresh()
					console.log(this.orderList)
				})
			},
			previewImage(e: any) {
				let current: any = e.target.dataset.src
				console.log(current)
				let urls: any = e.target.dataset.urls
				console.log(urls)
				uni.previewImage({
					urls: urls
				})
			}
		}
	})
