.uni-swiper-dots-horizontal {
	position: absolute;
	font-size: 0;
	margin-bottom: 40upx;
	transform-origin: 20% 40%;
}

.teng-option {
	position: relative;
}

.teng-options {
	position: absolute;
	left: -20%;
	top: -24upx;
	background-color: #fff;
	width: 140%;
	height: 80upx;
	border-radius: 50% / 100% 100% 0 0;
}

.search-swiper {
	position: relative;
}

.search {
	position: absolute;
	top: 20upx;
	left: 50%;
	transform: translate(-50%, 0);
	display: flex;
	justify-content: center;
	align-items: center;
	width: 90%;
	height: 60upx;
	background-color: rgba(255, 255, 255, 0.4);
	border-radius: 100upx;
	border: 2upx solid #d7d8da;
	z-index: 999;
}

.search-icon {
	width: 28upx;
	height: 28upx;
}

.search input {
	width: 20%;
	color: #707070;
	margin-left: 10upx;
	padding: 10upx 0 10upx 0;
}

.placeholder {
	color: #707070;
}

.teng-options-grid {
	background-color: #fff;
}

.swiper-box {
	height: 480upx;
}

.i-swiper-msg {
	border-top: 2upx solid #f4f4f4;
}

.i-swiper-msg-icon {
	width: 180upx;
	height: 80upx;
	margin-right: 20upx;
}

.uni-swiper-wrapper {
	position: relative;
	width: 90%;
	border-left: 2upx solid #f4f4f4;
	padding-left: 20upx;
}

.i-notice-original {
	color: #fe7f00;
}

.notice-more {
	width: 120upx;
	color: #6a6a6a;
	border-left: 4upx solid #f4f4f4;
	padding-left: 20upx;
	z-index: 10;
}

.i-item-msg {
	word-break: break-all;
	display: -webkit-box;
	overflow: hidden;
	line-height: 1.5;
	text-overflow: ellipsis;
	-webkit-box-orient: vertical;
	-webkit-line-clamp: 1;
}
