  import Vue from 'vue'
  import { request, navigateTo, showToast } from '@/common/utils/util'
  import { UserAddressListGet, UserAddressUpdate } from '@/common/config/api'
  import mpvueCityPicker from '@/components/mpvue-citypicker/mpvueCityPicker.vue'
	export default Vue.extend({
		components: {
			mpvueCityPicker
		},
		data() {
			return {
				AddressID: 0, // 收货地址ID 
				realName: '',
				Mobile: '',
				Province: '',
				City: '',
				Area: '',
				Address: '',
				ZipCode: '',
				IsDefault: 0,
        themeColor: '#fe7f00',
        cityPickerValueDefault: [0, 0, 1],
			}
		},
		onLoad(options: any) {
			let id = options.id
			this.AddressID = options.id
			this.getUserAddressList(id)
      console.log('onLoad', options)
		},
		methods: {
			getUserAddressList(id: any) {
				console.log(id)
				if (id == 0) {
					return;
				}
				let data = {
					AddressID: id
        }
		  	request(UserAddressListGet, data).then((res: any) => {
					console.log(res)
					let addressList = res.AddressList[0]
					this.realName = addressList.realName
					this.Mobile = addressList.Mobile
					this.Province = addressList.Province
					this.City = addressList.City
					this.Area = addressList.Area
					this.Address = addressList.Address
					this.ZipCode = addressList.ZipCode
					this.IsDefault = addressList.IsDefault
        })
      },
      // 三级联动选择
			showMulLinkageThreePicker() {
        console.log(this.$refs.mpvueCityPicker)
        let refs:any = this.$refs
				refs.mpvueCityPicker.show()
      },
      onCancel(e: any) {
        console.log(e)
      },
      onConfirm(e: any) {
				let label = e.label
				let address: any = label.split("-");
				let Province = address[0]
				let City = address[1]
				let Area = address[2]
				this.Province = Province
				this.City = City
				this.Area = Area
			},
			onrealNameInput(e: any) {
				this.realName = e.target.value
			},
			onMobileInput(e: any) {
				this.Mobile = e.target.value
			},
			onAddressInput(e: any) {
				this.Address = e.target.value
			},
			onZipCodeInput(e: any) {
        this.ZipCode = e.target.value
      },
			// 新增或修改收货地址
			updateAddress() {
				let AddressID = this.AddressID
				let realName = this.realName
				let Mobile = this.Mobile
				let Province = this.Province
				let City = this.City
				let Area = this.Area
				let Address = this.Address
				let ZipCode = this.ZipCode
				let IsDefault = this.IsDefault
				if (!AddressID) {
					AddressID = 0
				}
				if (!realName) {
					showToast('请填写收货人!')
					return;
				}
				if (!Mobile) {
					showToast('请填写收货人!')
					return;
				}
				if (!Province && !City && !Area) {
					showToast('请填写收货人!')
					return;
				}
				if (!Address) {
					showToast('请填写收货人!')
					return;
				}
				if (!IsDefault) {
					IsDefault = 0
				}
				let data = {
					AddressID: AddressID,
					realName: realName,
					Mobile: Mobile,
					Province: Province,
					City: City,
					Area: Area,
					Address: Address,
					ZipCode: ZipCode,
					IsDefault: IsDefault
        }
		  	request(UserAddressUpdate, data).then((res: any) => {
					console.log(res)
					showToast('保存成功！')
					navigateTo('../addressShipping/addressShipping')
        })
			}
    }
	})
