	.page-body {
		display: flex;
	}

	.nav {
		display: flex;
		width: 100%;
	}

	.nav-left {
		width: 30%;
	}

	.nav-left-item {
		height: 100upx;
		border-right: solid 1px #ededed;
		border-bottom: solid 1px #ededed;
		font-size: 30upx;
		display: flex;
		align-items: center;
		justify-content: center;
    background-color: #f3f4f3;
	}

	.nav-right {
		width: 70%;
	}

	.nav-right-item {
		width: 28%;
		height: 220upx;
		float: left;
		text-align: center;
		padding: 11upx;
		font-size: 28upx;
	}

	.nav-right-item image {
		width: 100upx;
		height: 100upx;
	}

	.active {
		color: #fe7f00;
    background-color: #e4e4e4;
	}
