.teng-contact {
	background-color: #fff;
	padding: 0 30upx;
}
.teng-contact-list {
	display: flex;
	justify-content: space-between;
	padding: 20upx 0;
}
.teng-contact-view {
	display: flex;
	align-items: center;
}
.teng-contact-view-image {
	width: 84upx;
	height: 100%;
}
.teng-contact-view image {
	height: 100%;
	padding: 10upx 0;
}
.teng-contact-text {
	margin-left: 16upx;
	font-size: 32upx;
	color: #616161;
}
.teng-contact-copy {
	color: #fe7f00;
}
.teng-help {
	padding: 10upx 30upx 0 30upx;
}
.teng-help-list-text {
	display: flex;
	align-items: flex-start;
	padding: 10upx 30upx;
}
.teng-help-list-text image {
	width: 36upx;
	height: 36upx;
	margin: 10upx 0;
}
.teng-help-list-text text {
	width: 100%;
	font-size: 28upx;
	color: #868686;
	margin-left: 20upx;
}
.teng-feedback {
	position: fixed;
	bottom: 0;
	width: 100%;
	height: 100upx;
}
.button {
	height: 100upx;
	font-size: 30upx;
	color: #616161;
	line-height: 100upx;
}

.button:after {
	border: none;
	height: 100upx;
}
