@charset "UTF-8";

.uni-list-item {
	font-size: 32upx;
	position: relative;
	display: flex;
	flex-direction: column;
	justify-content: space-between;
	align-items: center;
}

.uni-list-item--disabled {
	opacity: 0.3;
}

.uni-list-item--hover {
	background-color: #fff;
}

.uni-list-item__container {
	padding: 24upx 30upx;
	width: 100%;
	box-sizing: border-box;
	flex: 1;
	position: relative;
	display: flex;
	flex-direction: row;
	justify-content: space-between;
	align-items: center;
}

.uni-list-item__container:after {
	position: absolute;
	z-index: 3;
	right: 0;
	bottom: 0;
	left: 0;
	height: 1px;
	content: "";
	-webkit-transform: scaleY(0.5);
	transform: scaleY(0.5);
	background-color: #c8c7cc;
}

.uni-list-item__content {
	flex: 1;
	overflow: hidden;
	display: flex;
	flex-direction: column;
}

.uni-list-item__content-title {
	font-size: 30upx;
	color: #ababab;
	text-overflow: ellipsis;
	white-space: nowrap;
	color: inherit;
	line-height: 1.5;
	overflow: hidden;
}

.uni-list-item__content-note {
	color: #999;
	font-size: 28upx;
	white-space: normal;
	display: -webkit-box;
	-webkit-box-orient: vertical;
	-webkit-line-clamp: 2;
	overflow: hidden;
}

.uni-list-item__extra {
	width: 25%;
	display: flex;
	flex-direction: row;
	justify-content: flex-end;
	align-items: center;
}

.uni-list-item__icon {
	margin-right: 18upx;
	display: flex;
	flex-direction: row;
	justify-content: center;
	align-items: center;
}

.uni-list-item__icon-img {
	height: 52upx;
	width: 52upx;
}

.uni-list > .uni-list-item:last-child .uni-list-item-container:after {
	height: 0;
}
