result:
.content {
	background-color: #fff;
}

.mention-msg {
	display: flex;
	justify-content: space-between;
	align-items: center;
	height: 75upx;
	background-color: #f0f0f0;
	padding: 0 30upx;
}

.mention {
	display: flex;
	justify-content: flex-start;
	align-items: center;
}

.icon {
	width: 32upx;
	height: 32upx;
}

.mention-msg-text {
	font-size: 24upx;
	color: #767676;
	margin-left: 8upx;
}

.mention-money-msg {
	font-size: 24upx;
	line-height: 1.6;
	color: #767676;
}

.mention-money-no {
	font-size: 48upx;
	line-height: 1.6;
	color: #fe7f00;
	font-weight: 600;
}

.commission-list {
	display: flex;
	justify-content: space-between;
	align-items: center;
	border-bottom: 2upx solid #e1e1e1;
	margin: 0 30upx;
	padding: 30upx 0;
}

.commission-list:last-child {
	border-bottom: none;
}

.commission-created {
	font-size: 30upx;
	color: #616161;
}

.commission-createdDateTime {
	font-size: 28upx;
	color: #616161;
}

.commission-refUserNick {
	font-size: 28upx;
	color: #db8f42;
	margin-left: 8upx;
}

.commission-money {
	font-size: 32upx;
	color: #fa7d00;
}

.commission-money-text {
	margin-right: 12upx;
}

.commission-status-name {
	font-size: 20upx;
	color: #fff;
	background-color: #b3b3b3;
	border-radius: 8upx;
	padding: 8upx 12upx;
}

.commission-money-bg {
	color: #fa7d00;
}

.commission-status-bg {
	background-color: #fe7f00;
}

.mention-bottom {
	position: fixed;
	bottom: 0;
	width: 100%;
	height: 100upx;
	display: flex;
	align-items: center;
	padding: 0 30upx;
	border-top: 2upx solid #cdcdcd;
	z-index: 999;
}

.mention-bottom-text {
	font-size: 30upx;
	color: #888888;
}

.mention-bottom-money {
	font-size: 48upx;
	color: #fe7f00;
}
