@import "../mescroll-uni/mescroll-uni.css";
.mescroll-uni-bg {
	background-color: #fff;
}
/*下拉刷新--标语*/
.mescroll-downwarp .downwarp-slogan {
	display: block;
	width: 420upx;
	height: 168upx;
	margin: auto;
}
/*下拉刷新--向下进度动画*/
.mescroll-downwarp .downwarp-progress {
	display: inline-block;
	width: 40upx;
	height: 40upx;
	border: none;
	margin: auto;
	background-size: contain;
	background-repeat: no-repeat;
	background-position: center;
	background-image: url(https://api.tengpaisc.com/upload/guidang/icon_downwarp.png);
	-webkit-transition: all 300ms;
	transition: all 300ms;
}
/*下拉刷新--进度条*/
.mescroll-downwarp .downwarp-loading {
	display: inline-block;
	width: 32upx;
	height: 32upx;
	border-radius: 50%;
	border: 2upx solid #fe7f00;
	border-bottom-color: transparent;
}
/*下拉刷新--吉祥物*/
.mescroll-downwarp .downwarp-mascot {
	position: absolute;
	right: 16upx;
	bottom: 0;
	width: 100upx;
	height: 100upx;
	background-size: contain;
	background-repeat: no-repeat;
	-webkit-animation: animMascot 0.6s steps(1, end) infinite;
	animation: animMascot 0.6s steps(1, end) infinite;
}
