result:
page {
	background-color: #fff;
}
.list-cell {
	background-color: #fff;
}

.teng-order-body {
	display: flex;
	justify-content: flex-start;
}

.teng-order-list {
	margin: 0 30upx;
	padding: 20upx 0;
	border-bottom: 2upx solid #f4f4f4;
}

.teng-order-image {
	position: relative;
}

.teng-order-image-src {
	width: 200upx;
	height: 200upx;
}

.teng-order-show-status {
	position: absolute;
	left: 0;
	top: 0;
	width: 100upx;
	height: 100upx;
}

.teng-order-text {
	width: 100%;
	margin-left: 20upx;
}

.teng-order-title {
	padding: 10upx 0;
}

.teng-order-name {
	font-size: 36upx;
	color: #757575;
	display: -webkit-box;
	overflow: hidden;
	line-height: 1.5;
	-o-text-overflow: ellipsis;
	text-overflow: ellipsis;
	-webkit-box-orient: vertical;
	-webkit-line-clamp: 1;
}

.teng-order-num {
	font-size: 32upx;
	color: #757575;
	margin-left: 20upx;
}

.teng-order-placard-status {
	font-size: 24upx;
	line-height: 1.4;
	color: #fe7f00;
	border: 2upx solid #fe7f00;
	border-radius: 100upx;
	padding: 2upx 14upx;
}

.teng-order-number {
	font-size: 24upx;
	color: #909090;
	margin-top: 10upx;
}

.teng-order-msg {
	font-size: 28upx;
}

.teng-order-original {
	font-size: 32upx;
	color: #fe7f00;
}

.teng-order-status {
	font-size: 28upx;
	color: #666666;
}

.textarea {
	margin: 20upx 30upx;
	border-bottom: 2upx solid #f4f4f4;
}

.common-submit {
	position: fixed;
	bottom: 0;
	width: 100%;
}

.common-button {
	display: flex;
	justify-content: space-between;
	align-items: center;
}

.common-button button {
	height: 98upx;
	line-height: 98upx;
}

.btn-reset {
	width: 50%;
	color: #515151;
	background-color: #eeeeee;
}

.btn-submit {
	width: 50%;
	color: #ffffff;
	background-color: #fe7f00;
}
