import Vue from "vue";
export default Vue.extend({
	props: {
		options: {
			type: Object,
			default(e: any) {
				return {};
			}
		},
		statusDone: {
			type: Boolean,
			default: false
		},
		statusFlow: {
			type: Boolean,
			default: false
		}
	},
	data() {
		return {
			statusIconDone: "/static/icon/icon_done.png",
			statusIconFlow: "/static/icon/icon_flow.png"
		};
	},
	methods: {
		close(e: any) {
			this.$emit("close");
		},
		bindClick() {
			this.$emit("click");
		}
	}
});
