@charset "UTF-8";

.mask {
	position: fixed;
	z-index: 998;
	top: 0;
	right: 0;
	bottom: 0;
	left: 0;
	background-color: rgba(0, 0, 0, 0.3);
}

.popus-content {
	position: fixed;
	bottom: 0;
	width: 100%;
	z-index: 999;
	background-color: #ffffff;
}

.popus-head {
	font-size: 36upx;
	font-weight: 600;
	color: #232323;
	margin: 30upx 30upx 20upx 30upx;
}

.popus-remarks {
	font-size: 28upx;
	font-weight: 600;
	color: #fe0000;
	margin: 10upx 30upx 30upx 30upx;
}

.popus-purchase-num {
	font-size: 28upx;
	color: #5c5c5c;
	margin: 20upx 30upx 10upx 30upx;
}

.popus-num-box {
	margin: 10upx 30upx 40upx 30upx;
}

.popus-input {
	position: relative;
	background-color: #fff;
	width: 100%;
	height: 100%;
	text-align: center;
	padding: 0;
	margin: 0 30upx;
}

.popus-input:after {
	content: "";
	position: absolute;
	transform-origin: center;
	box-sizing: border-box;
	pointer-events: none;
	top: -50%;
	left: -50%;
	right: -50%;
	bottom: -50%;
	border-style: solid;
	border-color: #c8c7cc;
	border-left-width: 1px;
	border-right-width: 1px;
	border-top-width: 1px;
	border-bottom-width: 1px;
  border-radius: 8upx;
	transform: scale(0.5);
}

.i-numbox {
	display: flex;
	flex-direction: row;
	justify-content: space-between;
	height: 70upx;
	position: relative;
}

.i-numbox__minus,
.i-numbox__plus {
	margin: 0;
	background-color: #fff;
	width: 140upx;
	font-size: 40upx;
	height: 100%;
	line-height: 70upx;
	text-align: center;
	display: inline-flex;
	align-items: center;
	justify-content: center;
	color: #333;
	position: relative;
  margin: 0 20upx;
  border: 2upx solid #c8c7cc;
  border-radius: 8upx;
}

.i-numbox--disabled {
	color: silver;
}

.popus-totals-footer {
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 98upx;
  box-shadow: 2px 2px 5px #747474;
}

.popus-totals {
  width: 70%;
  text-align: center;
}

.popus-btn {
  width: 30%;
  margin: 0;
  line-height: 98upx;
  color: #fff;
  background-color: #fe7f00;
}
