	import { request, navigateTo } from "@/common/utils/util";
	import { OrderListGet } from "@/common/config/api";
	// 自定义的mescroll-beibei.vue
	import MescrollUni from "@/components/mescroll-diy/mescroll-beibei.vue";
	import mediaList from "@/components/tab-nvue/mediaList.vue";
	import PdList from "@/components/other/pd-list.vue";
	// 模拟数据
	import mockData from "@/common/pdlist.js";

	export default {
		components: {
			MescrollUni,
			mediaList,
			PdList
		},
		data() {
			return {
				scrollLeft: 0,
				isClickChange: false,
				current: 0,
				orderList: [], // 订单列表
				tabBars: [
					{
						name: "全部",
						id: "0",
						status: ""
					},
					{
						name: "待付款",
						id: "1",
						status: "1"
					},
					{
						name: "待发货",
						id: "2",
						status: "2"
					},
					{
						name: "待收货",
						id: "3",
						status: "3"
					},
					{
						name: "待晒单",
						id: "4",
						status: "4"
					}
				],
				pdList: [] // 数据列表
			}
		},
		onLoad(options) {
			console.log(options);
			let status = options.status;
			this.current = status;
		},
		methods: {
			/*下拉刷新的回调 */
			downCallback(mescroll) {
				// 下拉刷新的回调,默认重置上拉加载列表为第一页 (自动执行 mescroll.num=1, 再触发upCallback方法 )
				mescroll.resetUpScroll()
			},
			/*上拉加载的回调: mescroll携带page的参数, 其中num:当前页 从1开始, size:每页数据条数,默认10 */
			upCallback(mescroll) {
				//联网加载数据
				this.getListDataFromNet(mescroll.num, mescroll.size, (curPageData)=>{
					//联网成功的回调,隐藏下拉刷新和上拉加载的状态;
					console.log("mescroll.num=" + mescroll.num + ", mescroll.size=" + mescroll.size + ", curPageData.length=" + curPageData.length);
					mescroll.endSuccess(curPageData.length);
					//设置列表数据
					if(mescroll.num == 1) this.orderList = []; //如果是第一页需手动制空列表
					this.orderList=this.orderList.concat(curPageData); //追加新数据
				}, () => {
					//联网失败的回调,隐藏下拉刷新的状态
					mescroll.endErr();
				})
			},
			/*联网加载列表数据
			实际项目以您服务器接口返回的数据为准,无需本地处理分页.
			* */
			async getListDataFromNet(pageNum,pageSize,successCallback,errorCallback) {
        console.log(pageNum, pageSize)
				try{
          let orderList = await this.getOrderList(pageNum, pageSize)
          this.orderList = orderList
          console.log('data', this.orderList)
					//联网成功的回调
					successCallback && successCallback(orderList);
				} catch (e) {
					//联网失败的回调
					errorCallback && errorCallback();
				}
			},
		getOrderList(pageNum, pageSize) {
			return new Promise((sesolve, reject) => {
				let data = {
					OrderStatus: 2,
					PageID: pageNum,
					PageSize: pageSize
				};
				request(OrderListGet, data).then((res) => {
					console.log(res);
					sesolve(res.OrderList)
				});
			})
		},
		goDetail(e) {
			console.log(e);
			uni.navigateTo({
				url: "../orderDetail/orderDetail?id=" + e.OrderID
			});
		},
		async changeTab(e) {
			console.log(e);
		},
		async tapTab(e) {
			//点击tab-bar
			console.log(e);
			this.current = e.target.dataset.current;
		},
		randomfn() {
			let ary = [];
			for (let i = 0, length = this.tabBars.length; i < length; i++) {
				let aryItem = {
					loadingText: "加载更多...",
					data: []
				};
				ary.push(aryItem);
			}
			return ary;
		}
		}
	}
