.uni-mask {
	position: fixed;
	z-index: 998;
	top: 0;
	right: 0;
	bottom: 0;
	left: 0;
	background-color: rgba(0, 0, 0, 0.3);
}

.uni-popup {
	position: fixed;
	z-index: 999;
	background-color: #ffffff;
}

.uni-popup-middle {
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
}

.uni-popup-middle.uni-popup-insert {
	min-width: 380upx;
	min-height: 380upx;
	max-width: 100%;
	max-height: 80%;
	transform: translate(-50%, -65%);
	background: none;
	box-shadow: none;
}

.uni-popup-middle.uni-popup-fixed {
	border-radius: 10upx;
	padding: 30upx;
}

.uni-close-bottom,
.uni-close-right {
	position: absolute;
	bottom: -180upx;
	text-align: center;
	border-radius: 50%;
	color: #f5f5f5;
	font-size: 60upx;
	font-weight: bold;
	opacity: 0.8;
	z-index: -1;
}

.uni-close-bottom {
	margin: auto;
	left: 0;
	right: 0;
}

.uni-close-right {
	right: -60upx;
	top: -80upx;
}

.uni-close-bottom:after {
	content: '';
	position: absolute;
	width: 0px;
	border: 1px #f5f5f5 solid;
	top: -200upx;
	bottom: 56upx;
	left: 50%;
	transform: translate(-50%, -0%);
	opacity: 0.8;
}

.uni-popup-top,
.uni-popup-bottom {
	display: flex;
	align-items: center;
	justify-content: center;
}

.uni-popup-top {
	top: 0;
	left: 0;
	width: 100%;
	height: 100upx;
	line-height: 100upx;
	text-align: center;
}

.uni-popup-bottom {
	left: 0;
	bottom: 0;
	width: 100%;
	min-height: 100upx;
	line-height: 100upx;
}

.i-content {
	width: 100%;
	padding: 0 30upx;
}

.i-msg {
	font-size: 32upx;
	line-height: 80upx;
}

.i-textarea textarea{
	width: 94%;
	height: 260upx;
	border: 2upx solid #f4f4f4;
	border-radius: 4upx;
	padding: 20upx;
}

.i-feedback {
	display: flex;
	justify-content: center;
}

.i-feedback-button {
	width: 180upx;
	height: 60upx;
	line-height: 60upx;
	font-size: 36upx;
	text-align: center;
	border-radius: 4upx;
	border: none;
	color: #fff;
	background-color: #fe7f00;
	margin: 20upx 0 40upx 0;
}
