result:
	.page-body {
		display: flex;
	}

	.nav {
		display: flex;
		width: 100%;
	}

	.nav-left {
		width: 25%;
		border-right: solid 1px #ededed;
	}

	.nav-left-item {
		height: 100upx;
		border-bottom: solid 1px #ededed;
		font-size: 30upx;
		display: flex;
		align-items: center;
		justify-content: center;
    background-color: #f3f4f3;
	}

	.nav-right {
		width: 75%;
	}

	.nav-right-item {
		display: flex;
		justify-content: flex-start;
		align-items: center;
		height: 220upx;
		padding: 11upx;
		font-size: 28upx;
		border-bottom: 2upx solid #f4f4f4;
	}

	.teng-item-images {
		position: relative
	}

	.nav-right-item-image {
		width: 120upx;
		height: 120upx;
	}

	.teng-order-show-status {
		position: absolute;
		left: 0;
		top: 0;
		width: 80upx;
		height: 80upx;
	}

	.active {
		color: #fe7f00;
    background-color: #e4e4e4;
	}

	.teng-title {
    word-break: break-all;
    display: -webkit-box;
    overflow: hidden;
	  line-height:1.5;
    text-overflow: ellipsis;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 1;
	}

	.teng-content {
		width: 100%;
		margin-left: 10upx;
	}

	.teng-title {
		font-size: 28upx;
		line-height: 2.4;
		color: #757575;
	}

	.teng-type {
		display: flex;
		justify-content: space-between;
		align-items: center
	}

	.teng-type-text {
		font-size: 24upx;
		line-height: 1.6;
	}

	.teng-footer {
		display: flex;
		justify-content: space-between;
		align-items: center;
		margin-top: 10upx
	}

	.teng-pirce-text {
		font-size: 24upx;
		color: #333;
	}

	.teng-price-number {
		font-size: 32upx;
		color: #fe7f00;
	}

	.teng-btn {
		font-size: 24upx;
		line-height: 2.4;
		color: #fff;
		background-color: #fe7f00;
		border-radius: 10upx;
		margin: 0
	}
