result:
  page {
    background-color: #fff;
  }

	.teng-recharge {
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-wrap: wrap;
    background-color: #fff;
    padding: 20upx 10upx;
  }

  .teng-recharge-item {
    width: calc(33.33% - 30upx);;
    color: #333;
    background-color: #fff;
    border-radius: 20upx;
    text-align: center;
    padding: 40upx 0;
    border: 2upx solid #f4f4f4;
  }

  .teng-recharge-item-active {
    color: #fff;
    background-color: #ff8300
  }

  .teng-recharge-item-active .teng-recharge-price {
    color: #fff
  }

  .teng-recharge-item-active .teng-recharge-description {
    color: #fff
  }

  .teng-recharge-price {
    font-size: 36upx;
    color: #333;
  }

  .teng-recharge-description {
    font-size: 24upx;
    color: #5a5a5a
  }

  .teng-recharge-bottom {
    position: fixed;
    bottom: 0;
    width: 100%;
    height: 100upx;
    background-color: #f4f4f4;
  }

  .teng-recharge-total-price {
    font-size: 32upx;
    padding: 0 30upx;
  }

  .teng-recharge-total-price-number {
    color: #ff0000
  }

  .payment-btn {
    width: 240upx;
    margin: 0;
    border: none;
    border-radius: 0;
    line-height: 100upx;
  }
