result:
page {
	width: 100%;
	height: 100%;
}
.content {
	width: 100%;
	height: 100%;
}
.swiper {
	box-sizing: border-box;
	width: 100%;
	height: 100%;
	overflow: scroll;
}

.product-item {
	position: relative;
	display: inline-block;
	overflow: hidden;
	height: 98upx;
	line-height: 98upx;
}

.product-item .name {
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	font-weight: 600;
}

.active .name {
	color: #fe7f00;
}

.active .line {
	position: absolute;
	bottom: 0;
	left: 50%;
	transform: translate(-50%, 0);
	width: 100upx;
	height: 6upx;
	background-color: #fe7f00;
	border-radius: 8upx;
}

.i-tab-bar-loading {
	text-align: center;
	font-size: 28upx;
	color: #999;
}
.i-swiper-tab {
	white-space: nowrap;
	height: 98upx;
	line-height: 98upx;
	background-color: #fff;
	border-bottom: 2upx solid #f4f4f4;
}
.scroll-view-list {
	width: 100%;
	height: 100%;
}
