const path = require('path')
const webpack = require('webpack')

const {
  done
} = require('@vue/cli-shared-utils')

const {
  nvueJsPreprocessOptions
} = require('@dcloudio/uni-cli-shared')

const WebpackErrorsPlugin = require('@dcloudio/vue-cli-plugin-uni/packages/webpack-errors-plugin')

const onErrors = require('@dcloudio/vue-cli-plugin-uni/util/on-errors')

const vueLoaderConfig = require('./vue-loader.conf')

const jsPreprocessorLoader = {
  loader: '@dcloudio/vue-cli-plugin-uni/packages/webpack-preprocess-loader',
  options: nvueJsPreprocessOptions
}

const uniPath = process.env.UNI_USING_V8
  ? '../packages/uni-app-plus-nvue-v8/dist/index.js'
  : '../packages/uni-app-plus-nvue/dist/index.js'

const provide = {
  'uni': [path.resolve(__dirname, uniPath), 'default']
}
if (process.env.UNI_USING_V8) {
  provide['plus'] = [path.resolve(__dirname, uniPath), 'weexPlus']
}
const plugins = [
  new webpack.DefinePlugin({
    'process.env': {
      'NODE_ENV': JSON.stringify(process.env.NODE_ENV)
    }
  }),
  new webpack.BannerPlugin({
    banner: '// { "framework": "Vue"} \n',
    raw: true,
    exclude: 'Vue'
  }),
  new webpack.ProvidePlugin(provide),
  new WebpackErrorsPlugin({
    onErrors
  }),
  function (compiler) {
    compiler.hooks.done.tapPromise('WebpackAppPlusNVuePlugin', compilation => {
      return new Promise((resolve, reject) => {
        if (isFirst) {
          isFirst = false
        } else {
          if (process.env.NODE_ENV === 'development') {
            done(`Build complete. Watching for changes...`)
          } else {
            done(`Build complete. `)
          }
        }
        resolve()
      })
    })
  }
]

const excludeModuleReg = /node_modules(?!(\/|\\).*(weex).*)/

let isFirst = true

const vueLoaderOptions = vueLoaderConfig({
  useVue: false
})

const rules = [{
  test: /\.js$/,
  use: [{
    loader: 'babel-loader',
    options: {
      babelrc: false
    }
  },
  jsPreprocessorLoader
  ],
  exclude: excludeModuleReg
},
{
  test: /\.nvue(\?[^?]+)?$/,
  use: [{
    loader: 'weex-vue-loader',
    options: vueLoaderOptions
  }],
  exclude: excludeModuleReg
},
{
  test: /\.vue(\?[^?]+)?$/,
  use: [{
    loader: 'weex-vue-loader',
    options: vueLoaderOptions
  }],
  exclude: excludeModuleReg
}
]

if (process.env.UNI_USING_NVUE_COMPILER) {
  rules.unshift({
    test: path.resolve(process.env.UNI_INPUT_DIR, 'main.js'),
    use: [{
      loader: '@dcloudio/vue-cli-plugin-hbuilderx/packages/webpack-nvue-entry-loader'
    }]
  })
}

module.exports = function (entry) {
  return {
    mode: 'development', // process.env.NODE_ENV,
    devtool: false,
    watch: process.env.NODE_ENV === 'development',
    entry,
    output: {
      path: process.env.UNI_OUTPUT_DIR,
      filename: '[name].js'
    },
    resolve: {
      extensions: ['.js', '.nvue', '.vue', '.json'],
      alias: {
        '@': process.env.UNI_INPUT_DIR
      },
      modules: [
        'node_modules',
        path.resolve(process.env.UNI_INPUT_DIR, 'node_modules')
      ]
    },
    resolveLoader: {
      alias: {
        'babel-loader': require.resolve('babel-loader')
      }
    },
    module: {
      rules
    },
    plugins,
    stats: {
      reasons: true,
      errorDetails: true
    },
    node: {
      global: false,
      Buffer: false,
      __filename: false,
      __dirname: false,
      setImmediate: false,
      clearImmediate: false,
      assert: false,
      buffer: false,
      child_process: false,
      cluster: false,
      console: false,
      constants: false,
      crypto: false,
      dgram: false,
      dns: false,
      domain: false,
      events: false,
      fs: false,
      http: false,
      https: false,
      module: false,
      net: false,
      os: false,
      path: false,
      process: false,
      punycode: false,
      querystring: false,
      readline: false,
      repl: false,
      stream: false,
      string_decoder: false,
      sys: false,
      timers: false,
      tls: false,
      tty: false,
      url: false,
      util: false,
      vm: false,
      zlib: false
    }
  }
}
